import styled from "styled-components";
import krug from '../../../assets/img/code.ball.png'

type ElipsWrapperSkillType = {
    top: string
    left:string
    right:string
    bottom: string
}


export const ElipsWrapperSkill = styled.div<ElipsWrapperSkillType>`
  position: absolute;
  height: 6rem;
  width: 6rem;
  margin: 1em auto;
  //background: linear-gradient(#414141 0%, #2D2D2D 100%);
  border-radius: 50%;
  background-image: url("${krug}");
  z-index: 1;
  
  top: ${props => props.top};
  left: ${props => props.left};
  right: ${props => props.right};
  bottom: ${props => props.bottom};
  
  :before {
    content: "";
    display: inline-block;
    vertical-align: middle;
    padding-top: 100%;
  }
`
