import React from 'react';
import { FlexWrapper } from "../../../components/FlexWrapper";
import styled from "styled-components";
import photo_about_1 from '../../../assets/img/image_about_1.webp';
import { Container } from "../../../components/Container";
import { theme } from "../../../styles/Theme";
import photo_about_2 from '../../../assets/img/photo3.webp';
import photo_about_3 from '../../../assets/img/photo2.webp';
import { SectionTitle } from "../../../components/ScetionTitle";

type StyledContainerPhotoPropsType = {
    width?: string;
    height?: string;
    shadow?: string;
}

export const About = () => {
    return (
        <StyledAboutMe>
            <Container padding={'0, 0'}>
                <FlexWrapper margin={'114px 0'} position={'relative'} direction={'column'} align={'center'}>
                    <SectionTitle>ABOUT ME</SectionTitle>
                    <StyledContainer>
                        <StyledContainerPhoto width='330px' height='378px' shadow={'0 4px 4px 0 rgba(0, 0, 0, 0.25)'}>
                            <FlexWrapper justify={'center'} align={'center'}>
                                <StyledImg1 src={photo_about_1} />
                            </FlexWrapper>
                        </StyledContainerPhoto>
                        <StyledContainerPhoto width='307px' height='339px' shadow={'0 4px 4px 0 rgba(0, 0, 0, 0.25)'}>
                            <FlexWrapper justify={'center'} align={'center'}>
                                <StyledImg2 src={photo_about_2} />
                            </FlexWrapper>
                        </StyledContainerPhoto>
                        <StyledContainerPhoto width='496px' height='auto'>
                            <AboutText>
                                Hello again everyone! So, you already know that my name is Gleb. A little about myself:
                                student, 18 y.o., athlete-football player, I love creativity since childhood, I live in
                                Donetsk, Ukraine. Why programming? Everything is elementary - I like it, the profession
                                of the future, thanks to which I can provide myself and fulfill my dream - travel, at
                                the moment I specialize in web design, front-end and back-end development, turnkey
                                websites. Why should you choose me? I approach each order with great responsibility and
                                love, as I want to make a name for myself, exclude plagiarism and negligence, fully
                                study the project, the client and its target audience, work for quality, trying to make
                                an order as quickly and uniquely as possible, taking into account all the edits and
                                wishes. By trusting me, you will get the maximum return for your project, save your
                                nerves and time. If you are interested in me, you want to know something more or use my
                                services, then I will provide all my contacts below.
                            </AboutText>
                        </StyledContainerPhoto>
                        <StyledContainerPhoto width='310px' height='485px' shadow={'none'}>
                            <StyledImg3 src={photo_about_3} />
                        </StyledContainerPhoto>
                    </StyledContainer>
                </FlexWrapper>
            </Container>
        </StyledAboutMe>
    );
};

const StyledImg1 = styled.img`
  max-width: 272px;
  max-height: 320px;
  border: 4px solid ${theme.colors.border};
  margin-top: 8%;
`;

const StyledImg2 = styled.img`
  max-width: 250px;
  max-height: 281px;
  border: 4px solid ${theme.colors.border};
  margin-top: 8%;
`;

const StyledImg3 = styled.img`
  max-width: 287px;
  max-height: 427px;
  border: 4px solid ${theme.colors.border};
  margin-top: 8%;
`;

const StyledContainerPhoto = styled.div<StyledContainerPhotoPropsType>`
  width: ${props => props.width || '100px'};
  height: ${props => props.height || '100px'};
  box-shadow: ${props => props.shadow};
  background-color: ${theme.colors.secondaryBg};
  border-radius: 14px;
  margin: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AboutText = styled.p`
  margin: 20px;
  font-size: 18.5px;
  line-height: 1.5;
`;

const StyledAboutMe = styled.section`
  padding: 20px;
`;

const StyledContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: flex-start;
`;
