import React from 'react';
import styled from "styled-components";
import {theme} from "../../../../styles/Theme";
import {Container} from "../../../../components/Container";
import {FlexWrapper} from "../../../../components/FlexWrapper";

export const ContactForm = () => {
    return (
        <StyledContactForm>
            <Container>
                <FlexWrapper direction={'column'} justify={'space-around'}>
                    <StyledFormHeader>
                        Contact me, letâ€™s make magic together
                    </StyledFormHeader>
                    <FlexWrapper direction={'column'} gap={'15'}>
                        <Fields placeholder={'Name:'}/>
                        <Fields placeholder={'Email:'}/>
                        <Fields placeholder={'Message'} as={'textarea'}/>
                    </FlexWrapper>
                </FlexWrapper>
            </Container>
        </StyledContactForm>
    );
};

const StyledContactForm = styled.form`
  max-width: 300px;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-left: 400px;

  textarea {
    resize: none;
    height: 155px;
  }
`

const StyledFormHeader = styled.h3`
`
const Fields = styled.input`
  background-color: ${theme.colors.secondaryBg};

  &:focus-visible {
    outline: 1px solid ${theme.colors.accent};
  }
`
