import styled from "styled-components";
import {Icon} from "../../icon/Icon";
import React, { useRef, useEffect } from 'react';

// Определение интерфейса для пропсов
interface ElipsWrapperSkillType {
    top?: string;
    left?: string;
    right?: string;
    bottom?: string;
    iconId?: string; // Добавлен пропс для ID иконки из спрайта
}

const Wrapper = styled.div<ElipsWrapperSkillType>`
  position: absolute;
  height: 6rem;
  width: 6rem;
  margin: 1em auto;
  background: linear-gradient(#414141 0%, #2D2D2D 100%);
  border-radius: 50%;
  z-index: 1;

  top: ${props => props.top};
  left: ${props => props.left};
  right: ${props => props.right};
  bottom: ${props => props.bottom};

  display: flex;
  align-items: center;
  justify-content: center;

  svg {
    height: 41px;
    width: 47px;
    z-index: -1;
  }
`;

export const ElipsWrapperSkill: React.FC<ElipsWrapperSkillType> = ({ top, left, right, bottom, iconId }) => {
    const svgRef = useRef<SVGSVGElement>(null);

    useEffect(() => {
        if (svgRef.current && iconId) {
            const svgNamespace = "http://www.w3.org/2000/svg";
            const useElement = document.createElementNS(svgNamespace, "use");
            useElement.setAttributeNS("http://www.w3.org/1999/xlink", "href", `#${iconId}`);
            svgRef.current.appendChild(useElement);
        }
    }, [iconId]);

    return (
        <Wrapper top={top} left={left} right={right} bottom={bottom}>
            <svg ref={svgRef} />
        </Wrapper>
    );
};
